const Cell = ({
    id,
    cell,
    cells,
    setCells,
    turn,
    setTurn,
    won
}) => {

    const handleClick = (e) => {

        const taken = (cell.value !== "" ? false : true);

        if (!taken) {
            e.target.innerHTML = turn;
            const newArray = cells.map((cell, index) => {
                if (index === id) {
                    return turn;
                } else {
                    return cell
                }
            })
            setCells(newArray)
            setTurn(turn === "O" ? "X" : "O")
        }
    }


    return ( <
        div className = "square"
        id = {
            id
        }
        onClick = {
            !won && handleClick
        } > < /div>
    )
}

export default Cell
